import axios from 'axios';
import React, { useState, useEffect } from 'react';
import {TableRow, TableHead,TableContainer,AppBar, TableCell, TableBody, Table, Paper, TextField} from '@material-ui/core';
import CloseIcon from '@mui/icons-material/Close';
import DoneIcon from '@mui/icons-material/Done';
import Box from '@mui/material/Box';
// import Tabs from '@mui/material/Tabs';
// import Tab from '@mui/material/Tab';
// import Procurementapproval from './Procurementapproval';
// import Hrreject from './Hrreject';

 
export default function CenteredTabs() {
 const [value, setValue] = React.useState(0);
 const [data, setData] = useState([]);
 const [ashow, setAShow] = useState(false);
 const [rshow, setRShow] = useState(false);
 const [pshow, setPShow] = useState(true);
 
 const handleChange = (event, newValue) => {
   if(newValue == 0){
     setPShow(true);
     setAShow(false);
     setRShow(false);
   }else if(newValue == 1){
    setPShow(false);
    setAShow(true);
    setRShow(false);
  }
  else if(newValue == 2){
    setPShow(false);
    setAShow(false);
    setRShow(true);
  }
   setValue(newValue);
 };
   
  useEffect(()=> {
   console.log("trying to load hr candidate on page load")
     handleRefresh();
} ,[])
 
const handleRefresh = () => {
 axios.get('/procurement')
   .then((response) => {
     console.log(response)
     setData(response.data);
   }).catch((err) => {
     console.log(err);
   })
}

 
const handleApprove = (row, id) => {
 console.log(id)
  
   axios.post('/procurement/approve/'+row.id)
     .then((response) => {
       console.log('new item added',response);
       handleRefresh();
     }).catch((err) => {
       console.log(err)
     })
 }
 const handleReject = (row, id) => {
   console.log(id);
  
   axios.post('/procurement/reject/' +row.id,)
     .then((response) => {
       console.log(response);
       handleRefresh();
      
     }).catch((err) => {
       console.log(err);
     })
 }
  return (
   <TableContainer component={Paper}>
    <div key='div'>
       <AppBar position="static" key='abar'>
     <Tabs value={value} onChange={handleChange} centered>
       <Tab label="HR Page" key='ptab' />
       <Tab label="HR Approval Table" key='atab' />
       <Tab label="HR Reject Table" key='rtab' />
     </Tabs>
   </AppBar>
   </div>
   { pshow?
  <Table sx={{ minWidth: 450 }} aria-label="simple table" key='hpending'> 
        <TableHead>
          <TableRow>
           <TableCell align="center">Fname</TableCell>
           <TableCell align="center">Lname</TableCell>
           <TableCell align="center">DateOfBirth</TableCell>
           <TableCell align="center">Age</TableCell>
           <TableCell align="center">Experience</TableCell>
           <TableCell align="center">companyName</TableCell>
           <TableCell align="center">Status</TableCell>
           <TableCell align="center">Feedback</TableCell>
           <TableCell align="center">Action</TableCell>
          
         </TableRow>
       </TableHead>
        <TableBody>
         {data.map((row, id) => (
           <TableRow
             key={row.name}
             sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
           >
 
             <TableCell align="center">{row.fname}</TableCell>
             <TableCell align="center">{row.lname}</TableCell>
             <TableCell align="center">{row.dateofbirth}</TableCell>
             <TableCell align="center">{row.age}</TableCell>
             <TableCell align="center">{row.company}</TableCell>
             <TableCell align="center">{row.experience}</TableCell>
             <TableCell align="center">{row.status}</TableCell>
             <TableCell align="center">
             <TextField
                   autoFocus
                   margin="dense"
                   label="Feedback"
                   type="text"
                   variant="standard"
                   name="feedback"
                   value={row.feedback}
                   onChange={e => setFeedback(row, e.target.value)}
                  
                 /> 
            
            
              </TableCell>
             <TableCell align="center">
               <DoneIcon onClick={() => handleApprove(row, id)} />{' '}{' '}
               <CloseIcon onClick={() => handleReject(row, id)} />
             </TableCell>
           </TableRow>
         ))}
 
 
        
       </TableBody> 
     </Table> : ashow? <Hrapproval key='happr'></Hrapproval>:<Hrreject key='hrej'></Hrreject>
     }
    </TableContainer>
  );
}
