import React, { useEffect, useState } from 'react';
import { CommandBar, ICommandBarItemProps } from '@fluentui/react/lib/CommandBar';
import './Header.css';
import { Link } from 'react-router-dom';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
import LogoutIcon from '@mui/icons-material/Logout';
// import CircleNotificationsIcon from '@mui/icons-material/CircleNotifications';

const Header = (props) => {
    const [isLoggedIn, setLogged, luser, logoutHandler] = useState(false)
    useEffect(() => {
        // console.log(window.location.href == 'http://localhost:3000/');
        let loggedIn = localStorage.getItem('LoggedIn');
        let isAdmin = localStorage.getItem('isAdmin')
        console.log('loggedIn', loggedIn, 'isAdmin', isAdmin);
        if (loggedIn == 'true') {
            setLogged(true)
        }
        console.log(props)
    })
    const handleClick = () => {
        if (isLoggedIn) {
            localStorage.setItem('LoggedIn', false);
            localStorage.setItem('isAdmin', false);
            console.log(localStorage.getItem('isAdmin', 'LoggedIn'))
            setLogged(false);
            window.location.href = 'http://localhost:3000/';
        } else {
            window.location.href = 'http://localhost:3000/Login';
        }
    }
    return (
        <div className='divs'>

            <ul>
                <span hidden={window.location.href == 'http://localhost:3000/' ? false : true}>
                    <TextField
                        id="search"
                        label="search"
                        onChange={(e) => props.handleSearchFilter(e.target.value)}
                    /></span>
                <Link className='link' to={'/'}>Home</Link>{' '}
                {/* <Link className='link' to={'/Store'}>Store</Link>{' '} */}
                {/* <CircleNotificationsIcon> </CircleNotificationsIcon> */}
                <Link className='link' to={'/Procurement'}>Procurement</Link>{' '}
                <Link className='link' to={'/Finance'}>Finance</Link>{' '}
                <Link className='link' to={'/Logistics'}>Logistics</Link>{' '}
                <span style={{float:'right',}} hidden={props.hideLogin}>
                    <div>
                        {/* <LogoutIcon color="primary" /> */}
                        <b><Button color="secondary" variant="contained" onClick={() => handleClick()}>   {isLoggedIn ? 'Logout' : 'Login'} </Button></b>
                        <span>{ocount}</span>
           </div>
           { luser?
          <Button onClick={logoutHandler} style={btnstyle}>
									{"Logout"}
								</Button>:<Link href="login" variant="body2">
									{"Login"}
								</Link>  }
                  
                </span>{' '}
                <span style={{float:'right',color:'primary',margin:'5px'}}>
                    <AddShoppingCartIcon color='primary' onClick={props.handleClickOpen} />  {props.cartItemCount ?
                    <sup style={{color:'black',
                    fontSize:'12px',
                    background: 'blue',
                    borderRadius:'50%',
                    padding:'3px',
                    position:'relative',
                    left:'-8px',
                    top:'-10px',
                    opacity:0.9}}> {props.cartItemCount} </sup> : null}
                </span>


            </ul>

        </div>
    )
}
export default Header;
