import axios from 'axios';
import React, { useState, Fragment, useEffect } from 'react';
import { TableRow, TableHead, TableContainer, TableCell, TableBody, Table, Paper } from '@material-ui/core';
import RefreshIcon from '@mui/icons-material/Refresh';
import CloseIcon from '@mui/icons-material/Close';
import DoneIcon from '@mui/icons-material/Done';
export default function Procurement(props) {

  function createData(Title, Quantity, Price, Status) {
    return { Title, Quantity, Price, Status };
  }

  const [title, setTitle] = useState('');

  const [quantity, setQuantity] = useState('');
  const [price, setPrice] = useState('');
  const [status, setStatus] = useState('');
  const [data, setData] = useState([]);
  axios.post("http://localhost:8080/cart/procurement", {
    "title": title.itle,
    "quantity": quantity.quantity,
    "price": price.price,
    "status": status.status

  }).then((response) => {
    console.log(response);

  })
    .catch((error) => {
      console.log(error);
    })

  const handleRefresh = () => {
    axios.get('http://localhost:8080/cart/procurement')
      .then((response) => {
        console.log(response)
        setData(response.data);
      }).catch((err) => {
        console.log(err);
      })
  }
  const handleApprove = (row, id) => {
    console.log(row, id)
    let newArray = data;
    newArray[id].status = 'Approved'
    console.log(newArray)
    setData(newArray);
    // axios.put('http://localhost:8080/candidate/HR/'+id,{
    //   status:'Approve'
    // })
    // .then((response)=>{
    //   console.log(response);
    //   handleRefresh();
    // }).catch((err)=>{
    //   console.log(err);
    // })
    axios.post('http://localhost:8080/cart/1/PROCUREMENT_APPROVAL', {
      "title": row.Title,
      "quantity": row.quantity,
      "price": row.price
    })
      .then((response) => {
        console.log('new item added',response);
      }).catch((err) => {
        console.log(err)
      })
  }
  const handleReject = (row, id) => {
    console.log(row, id)
    let newArray = data;
    newArray[id].status = 'Reject'
    console.log(newArray)
    setData(newArray);
    axios.put('http://localhost:8080/cart/PROCUREMENT/' + id, {
      status: 'Reject'
    })
      .then((response) => {
        console.log(response);
        handleRefresh();
      }).catch((err) => {
        console.log(err);
      })
  }
  return (
    <TableContainer component={Paper}>
      <RefreshIcon onClick={handleRefresh} />
      <Table sx={{ minWidth: 450 }} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell align="center">Title</TableCell>
          
            <TableCell align="center">Quantity</TableCell>
            <TableCell align="center">Price</TableCell>
            <TableCell align="center">Status</TableCell>
            <TableCell align="center">Action</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {data.map((row, id) => (
            <TableRow
              key={row.name}
              sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
            >

              <TableCell align="center">{row.title}</TableCell>
            
              <TableCell align="center">{row.quantity}</TableCell>
              <TableCell align="center">{row.price}</TableCell>
              <TableCell align="center">{row.status}</TableCell>
              <TableCell hidden={row.status !== 'New'} align="center">
                <DoneIcon onClick={() => handleApprove(row, id)} />{' '}{' '}
                <CloseIcon onClick={() => handleReject(row, id)} />
              </TableCell>
            </TableRow>
          ))}


          {/* <td>
      <div class="dropout">
        <button class="more">
         <span></span>
         <span></span>
         <span></span>
        </button>
        <ul>
          <li>
            option 1
          </li>
          <li>
            option 2
          </li>
          <li>
            option 3
          </li>
        </ul>
      </div>
    </td> */}
        </TableBody>
      </Table>



    </TableContainer>
  );
}
