import React from 'react';
import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Grid';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useHistory } from "react-router-dom";
const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
}));

export default class Login extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            username: '',
            password: '',
            registerFlag:false,
            registerSuccess:false,
            userNotFoundError:false,
            registrationSuccessfull:false,
        }
        this.handleLogin= this.handleLogin.bind(this);
    }
    componentDidMount(){
        console.log(this.props)
    }
    
    handleChange = (e) => {
        this.setState({
            [e.target.id]: e.target.value
        })
    }

    // handleLogin = () => {
    //     console.log(this.state)
    //     let data={
    //         userName:this.state.username,
    //         password:this.state.password
    //     }
    //     axios.post('http://localhost:8080/login',data)
    //     .then((response)=>{
    //         console.log(response);
    //         if(response.data.response){
    //             console.log(response.data.response)
    //             let isAdmin = response.data.response.isAdmin
                
    //             this.props.getLoggedIn(true,isAdmin,response.data.response.userName)
    //             // this.props.navigate('/store');
    //             this.handleNav()
    //         }else{
    //             this.setState({
    //                 userNotFoundError:true
    //             })
    //         }
    //     }).catch((Err)=>{
    //         console.log(Err);
            
    //     })
    // }
    handleLogin = (event) => {
		event.preventDefault();
		this.setState({ loading: true });
		const userData = {
			email: this.state.email,
			password: this.state.password
		};
		axios
       .get('http://localhost:3001/getProcurement', {params:userData})
			.then((response) => {
				localStorage.setItem('AuthToken', `Bearer ${response.data}`);
				this.setState({
					loading: false
				});
				history.push('/');
				window.location.reload();
			})
			.catch((error) => {
				this.setState({
					errors: error.response.data,
					loading: false
				});
			});
	};


    handleNav=()=>{
        setTimeout(()=>{
            // this.props.navigate('/store')
        },2000)
    }

    handleRegister=()=>{
        console.log(this.state)
        let data={
            userName:this.state.username,
            password:this.state.password
        }
        axios.post('http://localhost:3001/signup',data)
        .then((response)=>{
            console.log(response);
            this.setState({
                userNotFoundError:false,
                registerSuccess:true,
            })
        }).catch((Err)=>{
            console.log(Err);
        })
    }

    render() {
        return (
            <div><br />
                <Grid container spacing={2}>

                    <Grid item xs={4}>
                        {/* <Item>xs=4</Item> */}
                    </Grid>
                    <Grid item xs={4}>
                        {this.state.registerFlag?<b>Register</b>:<b>Login</b>}<br/><br/>
                        {this.state.registerSuccess?<b>User Registered Successfully</b>:null}<br/><br/>
                        {this.state.userNotFoundError?<sup style={{color:'red'}}>User not found</sup>:null}<br/><br/>
                        
                        <TextField
                            required
                            id="username"
                            label="username"
                            onChange={this.handleChange}
                        /><br /><br />
                        <TextField
                            label="Password"
                            type="password"
                            id="password"
                            onChange={this.handleChange}
                            autoComplete="current-password" />
                    </Grid>
                    <Grid item xs={4}>
                        {/* <Item>xs=4</Item> */}
                    </Grid>
                    <Grid item xs={4}>

                    </Grid>
                    <Grid item xs={4}>
                        <Button hidden={this.state.registerFlag} onClick={() => this.handleLogin()} variant="contained"> Login</Button> 
                        <Button onClick={() => this.handleRegister()} variant="contained"> Register</Button> 
                        
                    </Grid>
                    <Grid item xs={4}>
                    
                    </Grid>


                </Grid>
            </div>
        )
    }
}
