import React from 'react';
import './table.css'
import Button from '@mui/material/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';
import TextField from '@mui/material/TextField';
import MenuItem from '@mui/material/MenuItem';
import Select from '@mui/material/Select';
import Stack from '@mui/material/Stack';
import AddShoppingCartIcon from '@mui/icons-material/AddShoppingCart';
// import Button from '@mui/material/Button';
import ButtonGroup from '@mui/material/ButtonGroup';
import axios from 'axios';
export default class Store extends React.Component {
    state = {
        products: [],
        viewDialog: false,
        formType: '',
        stock: '',
        projectID: '',
        requiredDate: null,
        quantity: 0,
        isLoggedIn:true
    }

    componentDidMount() {
        console.log(this.props)
        let loggedIn = localStorage.getItem('LoggedIn');
        let isAdmin = localStorage.getItem('isAdmin')
        console.log('loggedIn',loggedIn,'isAdmin',isAdmin);
        if(loggedIn=='true'){
            this.setState({
                isLoggedIn:false
            })
        }
        // axios.get('http://localhost:3001/products')
        //     .then((response) => {
        //         console.log(response)
        //         let items = [];
        //         items = response.data.data.map((element) => {
        //             let obj = {
        //                 id: element.id,
        //                 category: element.category,
        //                 price: element.price,
        //                 title: element.title,
        //                 rating: element.rating.rate
        //             }
        //             return obj;
        //         })
        //         this.setState({ products: items });
        //     }).catch((Err) => {
        //         console.log(Err)
        //     })
    }

    handleClickOpen = () => {
        this.setState({
            viewDialog: true
        })
    };
    handleIncrease = () => {
        this.setState({
            quantity: this.state.quantity + 1
        })
    }
    handleDecrease = () => {
    if(this.state.quantity>0){
        this.setState({
            quantity: this.state.quantity - 1
        })
    }
}

    handleClose = () => {
        this.setState({
            viewDialog: false
        })
    };

    handleDropdown = (event) => {
        this.setState({
            formType: event.target.value
        })
    };
    handlestock = (e) => {
        this.setState({
            stock: e.target.value
        })
    }
    handleProjectID = (e) => {
        this.setState({
            projectID: e.target.value
        })
    }
    handleDatePicker = (newValue) => {
        this.setState({
            requiredDate: newValue.target.value
        })
    };
    handleSubmit=()=> {
        let data = {
            projectID: this.state.projectID,
            quantity: this.state.quantity,
            requiredDate: this.state.requiredDate
        }
        axios.post('http://localhost:3001/newItem', data)
            .then((response) => {
                console.log(response);
                this.setState({
                    viewDialog: false
                })
            }).catch((Err) => {
                console.log(Err);
            })
    }
    render() {
        return (
            <div>
            <div style={{color:'black'}}>
                <h3> Store</h3>
                </div>
                <div style={{ alignItems: 'right'}}>
                    <Button disabled={this.state.isLoggedIn} onClick={this.handleClickOpen}>New Request</Button>
                </div>
                <Dialog open={this.state.viewDialog} onClose={this.handleClose}>
                    <DialogTitle>Request Form</DialogTitle>
                    <DialogContent>
                        <DialogContentText>
                            <Select
                                value={this.state.formType}
                                label="type"
                                fullWidth
                                onChange={this.handleDropdown}
                            >
                                <MenuItem value={'New'}>New Item</MenuItem>
                                <MenuItem value={'Current'}>Current Stock</MenuItem>
                            </Select>
                        </DialogContentText>
                        <Stack spacing={3}>

                            <TextField
                                autoFocus
                                id="projectID"
                                label="projectID"
                                fullWidth
                                onChange={this.handleProjectID}
                                variant="standard"
                            />
                            {/* <TextField
                                autoFocus
                                id="stock"
                                label="Stock"
                                fullWidth
                                onChange={this.handlestock}
                                variant="standard"
                            /> */}
                            <ButtonGroup variant="outlined" aria-label="outlined button group">
                                <Button onClick={this.handleIncrease}>+</Button>
                                <Button>{this.state.quantity}</Button>
                                <Button onClick={this.handleDecrease}>-</Button>
                            </ButtonGroup>

                            <input type="date" onSelect={this.handleDatePicker} ></input>
                        </Stack>
                    </DialogContent>
                    <DialogActions>
                        <Button onClick={this.handleClose}>Cancel</Button>
                        <Button onClick={this.handleSubmit}>Submit</Button>
                    </DialogActions>
                </Dialog>
                <table>
                    <thead>
                        <th>ID</th>
                        <th>Category</th>
                        <th>Title</th>
                        <th>Price</th>
                        <th>Rating</th>
                        {/* <th>Action</th> */}
                    </thead>
                    <tbody>
                        {this.state.products.length > 0 ? this.state.products.map((element) => {
                            return (
                                <tr key={element.id}>
                                    <td>{element.id}</td>
                                    <td>{element.category}</td>
                                    <td>{element.title}</td>
                                    <td>{element.price}</td>
                                    <td>{element.rating}</td>
                                    {/* <td><Button>Approve</Button><Button>Reject</Button></td> */}
                                </tr>
                            )
                        }) : null}
                    </tbody>
                </table>
            </div>
        )
    }
}
